import React, { Component } from 'react';
import './App.css';
import ImageLinkForm from './components/ImageLinkForm';
import ColorDetection from './components/ColorDetection';

const PAT = '67900a5135eb4d568e18a99946e5825f';
const USER_ID = 'zakhar_vd';       
const APP_ID = 'my-first-application';
const MODEL_ID = 'color-recognition';
const MODEL_VERSION_ID = 'dd9458324b4b45c2be1a7ba84d27cd04'; 
const IMAGE_URL = imageUrl;

const clarifaiRequest = () => {
  const raw = JSON.stringify({
    "user_app_id": {
        "user_id": USER_ID,
        "app_id": APP_ID
    },
    "inputs": [
        {
            "data": {
                "image": {
                    "url": IMAGE_URL
                }
            }
        }
    ]
});

return {
  method: 'POST',
  headers: {
      'Accept': 'application/json',
      'Authorization': 'Key ' + PAT
  },
  body: raw
};

}


class App extends Component {
  constructor() {
    super()
    this.state = {
      input: '',
      imageURL: ''
    }
  }

  onInputChange = (event) => {
    // updating the state
    this.setState = ({input: event.target.value});
  }

  onSubmit = () => {
    //updating the state
    this.setState = ({imageURL: this.state.input})
    fetch("https://api.clarifai.com/v2/models/" + MODEL_ID + "/versions/" + MODEL_VERSION_ID + "/outputs", clarifaiRequest(this.state.input))
      .then(response => response.json())
      .then(result => console.log('test', result))
      .catch(error => console.log('error', error));

  }

  render() {
    return (
      <div>
        <ImageLinkForm onInput={this.onInputChange} onSubmit={this.onSubmit}/>
        <ColorDetection imageUrl={this.state.imageURL}/>
      </div>
    );
  }
}

export default App;
