import React from "react";

const ImageLinkForm = ({ onInput, onSubmit }) => {
  return (
    <div className="w-auto flex justify-center mt-5">
      <div className="flex jusify-center flex-col items-center w-auto">
        <div className="p-3 mb-4">
          <h1 className="text-2xl text-center text-black-800 font-heading font-medium md:text-4xl">
            Detect colors from images using <span className="text-transparent bg-clip-text bg-gradient-to-r from-purple-500 to-blue-500">AI</span>
          </h1>
          <p className="text-sm md:text-lg text-center text-gray-500">Just paste the URL and let the magic happen!</p>
        </div>
        <div className="flex justify-center w-full">
          <input
            type="search"
            className="bg-white rounded w-2/3 p-1"
            placeholder="Image URL"
            onChange={onInput}
            onFocus={(e) => e.target.select()}
          />
          <button
            className="py-1 px-5 ml-1 bg-purple-500 text-white rounded hover:bg-purple-600"
            onClick={onSubmit}
          >
            Detect
          </button>
        </div>
      </div>
    </div>
  );
};

export default ImageLinkForm;
