import React from "react";

const ImageLinkForm = ({ onInput, onSubmit }) => {
  return (
    <div className="w-auto flex justify-center">
      <div className="flex jusify-center flex-col items-center w-auto">
        <div className="p-3">
          <h1 className="text-2xl text-black-800 font-inter font-medium md:text-2xl">
            Detect colors from an image using AI
          </h1>
          <p className="text-xs text-center text-gray-600">Just paste the URL!</p>
        </div>
        <div className="flex justify-center w-full">
          <input
            type="search"
            className="bg-white rounded w-2/3 p-1"
            placeholder="Image URL"
            onChange={onInput}
            onFocus={(e) => e.target.select()}
          />
          <button
            className="py-1 px-5 ml-1 bg-purple-500 text-white rounded hover:bg-purple-600"
            onClick={onSubmit}
          >
            Detect
          </button>
        </div>
      </div>
    </div>
  );
};

export default ImageLinkForm;
